    useEffect(() => {
        axios.get("/api/articles")
        .then((res) => {
            setTotalPosts(res.data);
        }).catch(error =>{
            console.log(error);
        })
    });

    return (
        <div>

        <div className="articleBody">

<header className= "articleHeader">
    <br/>
